export function glider(universe) {
	const newUniverse = JSON.parse(JSON.stringify(universe));
	const middleRow = Math.floor(newUniverse.length / 2);
	const middleCol = Math.floor(newUniverse[0].length / 2);

	newUniverse[middleRow][middleCol] = true;
	newUniverse[middleRow + 1][middleCol + 1] = true;
	newUniverse[middleRow + 2][middleCol - 1] = true;
	newUniverse[middleRow + 2][middleCol] = true;
	newUniverse[middleRow + 2][middleCol + 1] = true;

	return newUniverse;
}

export function oscillator(universe) {
	const newUniverse = JSON.parse(JSON.stringify(universe));
	const rowDiff = universe[0].length - oscillatorArr[0].length;
	let widerOscillator;

	if (rowDiff) {
		widerOscillator = oscillatorArr.map((array) =>
			array.concat(new Array(rowDiff).fill(false))
		);
		newUniverse.splice(0, 20, ...widerOscillator);
	} else {
		newUniverse.splice(0, 20, ...oscillatorArr);
	}

	return newUniverse;
}

export default function fillPreset(universe, preset) {
	const newUniverse = JSON.parse(JSON.stringify(universe));
	const colFit = Math.floor(universe[0].length / preset[0].length);
	const colDiff = universe[0].length - preset[0].length * colFit;
	const rowFit = Math.floor(universe.length / preset.length);

	// widen the preset
	let widerPreset = JSON.parse(JSON.stringify(preset));
	for (let j = 0; j < colFit - 1; j++) {
		widerPreset = widerPreset.map((array, i) => array.concat(preset[i]));
	}
	widerPreset = widerPreset.map((array) =>
		array.concat(new Array(colDiff).fill(false))
	);

	// fill it in as many rows as possible
	console.log(newUniverse.length);
	for (let i = 0; i < rowFit; i++) {
		const start = i * widerPreset.length;
		newUniverse.splice(start, widerPreset.length, ...widerPreset);
	}

	return newUniverse;
}

